    private void executar(String op)
    {   
        Modelo.Controle controle = new Controle();
        
        controle.setOp(op);
        controle.setNum1(txfNum1.getText());
        controle.setNum2(txfNum2.getText());
        
        controle.calcular();

        if(controle.getErrorMessage().equals(""))
        {
            
            txfResult.setText(controle.getResultado());
            
        }
        
        else
        {
            JOptionPane.showMessageDialog(null, controle.getErrorMessage());
            txfNum1.setText("");
            txfNum2.setText("");
            txfNum1.requestFocus();
        }
    }
    private void btnDividedActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnDividedActionPerformed
    {//GEN-HEADEREND:event_btnDividedActionPerformed

        this.executar("/");

    }//GEN-LAST:event_btnDividedActionPerformed

