    private void txfNum1KeyPressed(java.awt.event.KeyEvent evt)//GEN-FIRST:event_txfNum1KeyPressed
    {//GEN-HEADEREND:event_txfNum1KeyPressed
        
        if(evt.getKeyCode() == KeyEvent.VK_ENTER)
            txfNum2.requestFocus();
    }//GEN-LAST:event_txfNum1KeyPressed

    
